		long area = LOBYTE(x);	// 所在区号
		if ( area < 0xB0 || area > 0xF7 )
			return "";
		long area_index = HIBYTE(x);	// 区内编号
		if ( area == 0xD7 )	// 55区(D7F9)处，空5个汉字
		{
			if ( area_index < 0xA1 || area_index > 0xF9 )	
				return "";
		}
		else
		{
			if ( area_index < 0xA1 || area_index > 0xFE )
				return "";
		}

		size_t idx = (area - 0xB0) * 94 + area_index - 0xA1;	// 第一区为B0，每区94个汉字，每区第一个汉字以A1开始编号
		if ( area > 0xD7 )	// 55区(D7F9)处，空5个汉字
			idx -= 5;

		return WordIndex[idx];

